function fixURL(obj) {
    if (obj === null) {
        return null;
    }
    else if (Array.isArray(obj)) {
        return obj.map(fixURL);
    }
    else if(typeof obj === "object") {
        var newObj = {};
        Object.keys(obj).forEach(function (key) {
            var val = obj[key];
            if (key === "url") {
                newObj[key] = "https://raw.githubusercontent.com/vega/vega/master/docs/" +val;
            }
            else {
                newObj[key] = fixURL(val);
            }
        });
        return newObj;
    }
    return obj;
}

msg = {
    payload: fixURL(msg.payload)
};
return msg;

